// CommentList.js
import React from 'react';

import './CommentList.css';
import Comment from '../Comment/Comment';

const CommentList = ({ comments, onDelete, onEdit }) => {
  return (
    <div className="comments-section">
      <h5>Comments:</h5>
      {comments.map((comment) => (
        <Comment
          key={comment._id}
          comment={comment}
          onDelete={onDelete}
          onEdit={onEdit}
        />
      ))}
    </div>
  );
};

export default CommentList;
