import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './DetailedPostView.css'; // Import the CSS file
import Comments from '../Comments/Comments';

const DetailedPostView = () => {
  const [post, setPost] = useState(null);
  const { id } = useParams(); // Grab :id from URL

  useEffect(() => {
    const fetchPostAndComments = async () => {
      const postResponse = await axios.get(`/api/posts/${id}`);
      setPost(postResponse.data.data.post);
      const commentsResponse = await axios.get(`/api/comments/${id}`);
      setComments(commentsResponse.data.data.comments);
    };
    fetchPostAndComments();
  }, [id]);

  const handleCommentAdded = (comment) => {
    setComments([...comments, comment]);
  };

  if (!post) return <div>Loading...</div>;

  return (
    <div className="detailed-post-container">
      <h1 className="detailed-post-title">{post.title}</h1>
      <p className="detailed-post-content">{post.content}</p>
      <CommentList comments={comments} />
      <CommentForm postId={post._id} onCommentAdded={handleCommentAdded} />
    </div>
  );
};

export default DetailedPostView;
