// Comment.js
import React from 'react';
import './Comment.css'; // Import the CSS file
import { FaEdit, FaTrash } from 'react-icons/fa';

const Comment = ({ comment, onDelete, onUpdate }) => {
  const [isEditMode, setIsEditMode] = useState(false);
  const [editedText, setEditedText] = useState(comment.text);

  const handleEditClick = () => {
    setIsEditMode(true);
  };

  const handleSaveClick = () => {
    onUpdate({ ...comment, text: editedText });
    setIsEditMode(false);
  };

  const handleTextChange = (e) => {
    setEditedText(e.target.value);
  };

  return (
    <div className="comment-container">
      <span className="comment-author">{comment.author}</span>
      <p className="comment-text">{comment.text}</p>
      <div className="comment-actions">
        <button onClick={() => onUpdate(comment)} className="edit-button">
          <FaEdit /> Edit
        </button>
        <button onClick={() => onDelete(comment._id)} className="delete-button">
          <FaTrash /> Delete
        </button>
      </div>
    </div>
  );
};

export default Comment;
