import React from "react";
import { useNavigate } from "react-router-dom";

// Post component used in PostList
const Post = ({ post }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/posts/${post._id}`);
  };

  return (
    <div>
      <h4>{post.title}</h4>
      {/* Display a brief summary or part of the post content */}
      <p>{post.content.substring(0, 100)}...</p>
      {/* Link to the detailed post view */}
      <button onClick={handleClick}>View Post</button>
    </div>
  );
};

export default Post;
